package nu.mine.wberg.main;import nu.mine.wberg.query.MovieData;import nu.mine.wberg.query.MovieSource;import nu.mine.wberg.query.MovieSourceFactory;import java.util.List;public class Main {    private static final int NAME_COLUMN_WIDTH = 40;    private static final int DIRECTOR_COLUMN_WIDTH = 30;    private static final int YEAR_COLUMN_WIDTH = 4;    public static void main(String... args) {        MovieSourceFactory movieSourceFactory = new MovieSourceFactory();        List<MovieData> movieDatas;        try {            MovieSource movieSource = movieSourceFactory.getMovieSource(System.getProperty("api"));            movieDatas = movieSource.getMovieData(System.getProperty("movie"));        }        catch (IllegalArgumentException e) {            System.err.println(e.getMessage());            return;        }        for (MovieData movieData : movieDatas) {            System.out.printf("%-" + NAME_COLUMN_WIDTH + "s " +                    "%-" + DIRECTOR_COLUMN_WIDTH + "s " +                    "%-" + YEAR_COLUMN_WIDTH + "s " +                    "%n",                    movieData.getName(),                    movieData.getDirector(),                    movieData.getYear());        }    }}